// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import "@openzeppelin/contracts/utils/Strings.sol";

library SemverLibrary {
    uint8 internal constant ASCII_ZERO = 48;

    function numberifySemver(string memory _semver) internal pure returns (uint256) {
        uint256[3] memory res;
        uint256 semverIndex;
        uint256 semverLength;
        for (uint256 i = 0; (i < bytes(_semver).length) && (semverIndex < 3); i++) {
            uint8 b = uint8(bytes(_semver)[i]);
            if (b == uint8(bytes1("."))) {
                // forbid empty semver part
                if (semverLength == 0) {
                    return 0;
                }
                semverIndex += 1;
                semverLength = 0;
                continue;
            }
            if (b < ASCII_ZERO || b > ASCII_ZERO + 9) {
                return 0;
            }
            res[semverIndex] = res[semverIndex] * 10 + b - ASCII_ZERO;
            semverLength += 1;
        }
        if ((semverIndex != 2) || (semverLength == 0)) {
            return 0;
        }
        return (res[0] << 16) + (res[1] << 8) + res[2];
    }

    function stringifySemver(uint256 num) internal pure returns (string memory) {
        if (num >= 1 << 24) {
            return "0";
        }
        string memory major = Strings.toString(num >> 16);
        string memory minor = Strings.toString((num >> 8) & 0xff);
        string memory patch = Strings.toString(num & 0xff);
        return string(abi.encodePacked(abi.encodePacked(major, ".", minor, ".", patch)));
    }
}

contracts/utils/ContractMeta.sol.content:
// SPDX-License-Identifier: BSL-1.1
pragma solidity 0.8.9;

import "../interfaces/utils/IContractMeta.sol";

abstract contract ContractMeta is IContractMeta {
    // -------------------  EXTERNAL, VIEW  -------------------

    function contractName() external pure returns (string memory) {
        return _bytes32ToString(_contractName());
    }

    function contractNameBytes() external pure returns (bytes32) {
        return _contractName();
    }

    function contractVersion() external pure returns (string memory) {
        return _bytes32ToString(_contractVersion());
    }

    function contractVersionBytes() external pure returns (bytes32) {
        return _contractVersion();
    }

    // -------------------  INTERNAL, VIEW  -------------------

    function _contractName() internal pure virtual returns (bytes32);

    function _contractVersion() internal pure virtual returns (bytes32);

    function _bytes32ToString(bytes32 b) internal pure returns (string memory s) {
        s = new string(32);
        uint256 len = 32;
        for (uint256 i = 0; i < 32; ++i) {
            if (uint8(b[i]) == 0) {
                len = i;
                break;
            }
        }
        assembly {
            mstore(s, len)
            mstore(add(s, 0x20), b)
        }
    }
}

