}

contracts/vaults/AaveVault.sol.content:
// SPDX-License-Identifier: agpl-3.0
pragma solidity 0.8.9;

import "@openzeppelin/contracts/utils/introspection/ERC165.sol";

import "../interfaces/external/aave/ILendingPool.sol";
import "../interfaces/vaults/IAaveVaultGovernance.sol";
import "../interfaces/vaults/IAaveVault.sol";
import "../libraries/ExceptionsLibrary.sol";
import "./IntegrationVault.sol";

/// @notice Vault that interfaces Aave protocol in the integration layer.
/// @dev Notes:
/// **TVL**
///
/// The TVL of the vault is cached and updated after each deposit withdraw.
/// So essentially `tvl` call doesn't take into account accrued interest / donations to Aave since the
/// last `deposit` / `withdraw`
///
/// **aTokens**
/// aTokens are fixed at the token creation and addresses are taken from Aave Lending Pool.
/// So essentially each aToken is fixed for life of the AaveVault. If the aToken is missing for some vaultToken,
/// the AaveVault cannot be created.
///
/// **Push / Pull**
/// It is assumed that any amounts of tokens can be deposited / withdrawn from Aave.
/// The contract's vaultTokens are fully allowed to Aave Lending Pool.
contract AaveVault is IAaveVault, IntegrationVault {
    using SafeERC20 for IERC20;
    address[] internal _aTokens;
    uint256[] internal _tvls;
    uint256 private _lastTvlUpdateTimestamp;
    ILendingPool private _lendingPool;

    // -------------------  EXTERNAL, VIEW  -------------------

    /// @inheritdoc IVault
    function tvl() public view override returns (uint256[] memory minTokenAmounts, uint256[] memory maxTokenAmounts) {
        minTokenAmounts = _tvls;
        maxTokenAmounts = new uint256[](minTokenAmounts.length);
        uint256 timeElapsed = block.timestamp - _lastTvlUpdateTimestamp;
        uint256 factor = CommonLibrary.DENOMINATOR;
        if (timeElapsed > 0) {
            uint256 apy = IAaveVaultGovernance(address(_vaultGovernance)).delayedProtocolParams().estimatedAaveAPY;
            factor = CommonLibrary.DENOMINATOR + FullMath.mulDiv(apy, timeElapsed, CommonLibrary.YEAR);
        }
        for (uint256 i = 0; i < minTokenAmounts.length; i++) {
            maxTokenAmounts[i] = FullMath.mulDiv(factor, minTokenAmounts[i], CommonLibrary.DENOMINATOR);
        }
    }

    /// @inheritdoc IAaveVault
    function lendingPool() external view returns (ILendingPool) {
        return _lendingPool;
    }

    /// @inheritdoc IERC165
    function supportsInterface(bytes4 interfaceId) public view override(IERC165, IntegrationVault) returns (bool) {
        return IntegrationVault.supportsInterface(interfaceId) || interfaceId == type(IAaveVault).interfaceId;
    }

    // -------------------  EXTERNAL, MUTATING  -------------------

    /// @notice Update all tvls to current aToken balances.
    function updateTvls() external {
        _updateTvls();
    }

    /// @inheritdoc IAaveVault
    function initialize(uint256 nft_, address[] memory vaultTokens_) external {
        _initialize(vaultTokens_, nft_);
        _lendingPool = IAaveVaultGovernance(address(_vaultGovernance)).delayedProtocolParams().lendingPool;
        _aTokens = new address[](vaultTokens_.length);
        for (uint256 i = 0; i < vaultTokens_.length; ++i) {
            address aToken = _getAToken(vaultTokens_[i]);
            require(aToken != address(0), ExceptionsLibrary.ADDRESS_ZERO);
            _aTokens[i] = aToken;
            _tvls.push(0);
        }
        _lastTvlUpdateTimestamp = block.timestamp;
    }

    // -------------------  INTERNAL, VIEW  -------------------

    function _getAToken(address token) internal view returns (address) {
        DataTypes.ReserveData memory data = _lendingPool.getReserveData(token);
        return data.aTokenAddress;
    }

    function _isReclaimForbidden(address token) internal view override returns (bool) {
        uint256 len = _aTokens.length;
        for (uint256 i = 0; i < len; ++i) {
            if (_aTokens[i] == token) {
                return true;
            }
        }
        return false;
    }

    // -------------------  INTERNAL, MUTATING  -------------------

    function _updateTvls() private {
        uint256 tvlsLength = _tvls.length;
        for (uint256 i = 0; i < tvlsLength; ++i) {
            _tvls[i] = IERC20(_aTokens[i]).balanceOf(address(this));
        }
        _lastTvlUpdateTimestamp = block.timestamp;
    }

    function _push(uint256[] memory tokenAmounts, bytes memory options)
        internal
        override
        returns (uint256[] memory actualTokenAmounts)
    {
        address[] memory tokens = _vaultTokens;
        uint256 referralCode = 0;
        if (options.length > 0) {
            referralCode = abi.decode(options, (uint256));
        }

        for (uint256 i = 0; i < _aTokens.length; ++i) {
            if (tokenAmounts[i] == 0) {
                continue;
            }
            address token = tokens[i];
            IERC20(token).safeIncreaseAllowance(address(_lendingPool), tokenAmounts[i]);
            _lendingPool.deposit(tokens[i], tokenAmounts[i], address(this), uint16(referralCode));
            IERC20(token).safeApprove(address(_lendingPool), 0);
        }
        _updateTvls();
        actualTokenAmounts = tokenAmounts;
    }

    function _pull(
        address to,
        uint256[] memory tokenAmounts,
        bytes memory
    ) internal override returns (uint256[] memory actualTokenAmounts) {
        address[] memory tokens = _vaultTokens;
        actualTokenAmounts = new uint256[](tokenAmounts.length);
        for (uint256 i = 0; i < _aTokens.length; ++i) {
            if ((_tvls[i] == 0) || (tokenAmounts[i] == 0)) {
                continue;
            }
            uint256 balance = IERC20(_aTokens[i]).balanceOf(address(this));
            uint256 amount = tokenAmounts[i] < balance ? tokenAmounts[i] : balance;
            actualTokenAmounts[i] = _lendingPool.withdraw(tokens[i], amount, to);
        }
        _updateTvls();
    }
}

contracts/vaults/YearnVault.sol.content:
// SPDX-License-Identifier: BSL-1.1
pragma solidity 0.8.9;

import "../interfaces/external/yearn/IYearnProtocolVault.sol";
import "../interfaces/vaults/IYearnVaultGovernance.sol";
import "../interfaces/vaults/IYearnVault.sol";
import "./IntegrationVault.sol";

/// @notice Vault that interfaces Yearn protocol in the integration layer.
/// @dev Notes:
/// **TVL**
///
/// The TVL of the vault is updated after each deposit withdraw.
///
/// **yTokens**
/// yTokens are fixed at the token creation and addresses are taken from YearnVault governance and if missing there
/// - in YearnVaultRegistry.
/// So essentially each yToken is fixed for life of the YearnVault. If the yToken is missing for some vaultToken,
/// the YearnVault cannot be created.
///
/// **Push / Pull**
/// There are some deposit limits imposed by Yearn vaults.
/// The contract's vaultTokens are fully allowed to corresponding yTokens.

contract YearnVault is IYearnVault, IntegrationVault {
    using SafeERC20 for IERC20;
    uint256 public constant DEFAULT_MAX_LOSS = 10000; // 10000%%

    address[] private _yTokens;

    /// @notice Yearn protocol vaults used by this contract
    function yTokens() external view returns (address[] memory) {
        return _yTokens;
    }

    // -------------------  EXTERNAL, VIEW  -------------------

    /// @inheritdoc IVault
    function tvl() public view override returns (uint256[] memory minTokenAmounts, uint256[] memory maxTokenAmounts) {
        address[] memory tokens = _vaultTokens;
        minTokenAmounts = new uint256[](tokens.length);
        for (uint256 i = 0; i < _yTokens.length; ++i) {
            IYearnProtocolVault yToken = IYearnProtocolVault(_yTokens[i]);
            minTokenAmounts[i] = FullMath.mulDiv(
                yToken.balanceOf(address(this)),
                yToken.pricePerShare(),
                10**yToken.decimals()
            );
        }
        maxTokenAmounts = minTokenAmounts;
    }

    /// @inheritdoc IntegrationVault
    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(IERC165, IntegrationVault)
        returns (bool)
    {
        return super.supportsInterface(interfaceId) || type(IYearnVault).interfaceId == interfaceId;
    }

    // -------------------  EXTERNAL, MUTATING  -------------------
    /// @inheritdoc IYearnVault
    function initialize(uint256 nft_, address[] memory vaultTokens_) external {
        _initialize(vaultTokens_, nft_);
        _yTokens = new address[](vaultTokens_.length);
        for (uint256 i = 0; i < vaultTokens_.length; ++i) {
            _yTokens[i] = IYearnVaultGovernance(address(msg.sender)).yTokenForToken(vaultTokens_[i]);
            require(_yTokens[i] != address(0), ExceptionsLibrary.ADDRESS_ZERO);
        }
    }

    // -------------------  INTERNAL, VIEW  -----------------------
    function _isReclaimForbidden(address token) internal view override returns (bool) {
        uint256 len = _yTokens.length;
        for (uint256 i = 0; i < len; ++i) {
            if (_yTokens[i] == token) {
                return true;
            }
        }
        return false;
    }

    // -------------------  INTERNAL, MUTATING  -------------------

    function _push(uint256[] memory tokenAmounts, bytes memory)
        internal
        override
        returns (uint256[] memory actualTokenAmounts)
    {
        address[] memory tokens = _vaultTokens;
        actualTokenAmounts = tokenAmounts;
        for (uint256 i = 0; i < _yTokens.length; ++i) {
            if (tokenAmounts[i] == 0) {
                continue;
            }

            address token = tokens[i];
            IYearnProtocolVault yToken = IYearnProtocolVault(_yTokens[i]);
            IERC20(token).safeIncreaseAllowance(address(yToken), tokenAmounts[i]);
            try yToken.deposit(tokenAmounts[i], address(this)) returns (uint256) {} catch (bytes memory) {
                actualTokenAmounts[i] = 0;
            }
            IERC20(token).safeApprove(address(yToken), 0);
        }
    }

    function _pull(
        address to,
        uint256[] memory tokenAmounts,
        bytes memory options
    ) internal override returns (uint256[] memory actualTokenAmounts) {
        actualTokenAmounts = new uint256[](tokenAmounts.length);
        uint256 maxLoss = options.length > 0 ? abi.decode(options, (uint256)) : DEFAULT_MAX_LOSS;
        for (uint256 i = 0; i < _yTokens.length; ++i) {
            if (tokenAmounts[i] == 0) continue;

            IYearnProtocolVault yToken = IYearnProtocolVault(_yTokens[i]);
            uint256 yTokenAmount = FullMath.mulDiv(tokenAmounts[i], (10**yToken.decimals()), yToken.pricePerShare());
            uint256 balance = yToken.balanceOf(address(this));
            if (yTokenAmount > balance) {
                yTokenAmount = balance;
            }

            if (yTokenAmount == 0) continue;

            actualTokenAmounts[i] = yToken.withdraw(yTokenAmount, to, maxLoss);
        }
    }
}

